// JavaScript Document
$(document).ready(function () {
    $('#Top .Tpbar .CentreBox .Menu .Tpnva li').mouseenter(function () {
        var index = $(this).parent().children().index(this);
        $(this).parent().children().each(function () {
            if ($(this).hasClass('Select')) {
                $(this).removeClass('Select');
            }
        });
        $(this).addClass('Select');

        $('#Top .twbar .CentreBox .Menu').each(function () {
            if (!$(this).hasClass('Hide')) {
                $(this).addClass('Hide');
            }
        });
        $('#Top .twbar .CentreBox .Menu').eq(index).removeClass('Hide');
    });

    $('#Top .twbar .CentreBox .Menu ul li a').mouseenter(function () {
        var index = $('#Top .twbar .CentreBox .Menu ul li a').index(this);
        $('#Top .twbar .CentreBox .Menu ul li').each(function () {
            if ($(this).hasClass('Select')) {
                $(this).removeClass('Select');
            }
        });
        $(this).parent().addClass('Select');
    });
});